import React from "react";
import { NavLink } from "react-router-dom";
import MySearch from "../MySearch/MySearch";
import cl from "./NavBar.module.css";
import { REGISTRATION_ROUTE } from "../../consts/consts";



const NavBar=({list, setTheme,theme,men, setMenu,authMenu,search,setSearch,modal,setModal})=>{

    const changeBar=(p)=> {

        if(p.title=="Войти"){
            setMenu(authMenu)

        }
        
        
    }

    const onClickF=(p)=>{
        
      
       if(p.title=="Войти")
       {
        
         
      
             return(<NavLink to={REGISTRATION_ROUTE}/>)
         }
       
     
     }
   

    
    var i=1;



    return(
        <div className={cl.main}>
           
            <div className={cl.tap} onClick={()=>setTheme(!theme)}></div>
            <h2 className={cl.logo}>ВсеЗапчасти</h2>
            <ul className={cl.NavBar}>
                {men.map((p)=>
              <a key={i++} onClick={()=>onClickF(p)} className={cl.a} href={p.href}> <li key={i+90}  className={cl.li}>{p.title}</li></a> )}
              
            </ul>
            <MySearch list={list} search={search} setSearch={setSearch}></MySearch>
           
        </div>
    )
}


export default NavBar;
