import React, { useContext } from "react";
import { useState } from "react";
import { Context } from "..";
import DeviceStore from "../store/DeviceStore";


export const ModalAdd=({mod,setMod})=>{ 

    var i=10;
    

    const {device}=useContext(Context)
    const [dev, setDev]=useState({id:i++,name:'',desctiprion:"", price:""})
    
    
    const clicker=async()=>{
       
        console.log(dev)
       
       device.setDevices([...device.devices,dev])
       console.log(device.devices)
       
        setMod(false)
    }



    return( 
        <div className={mod?'modal':'modal_none'} onClick={()=>setMod(false)}> 
        <div className="inner" onClick={(e)=>e.stopPropagation()}>
            {/* <select>
               {device.types.map((e)=>{ 
                return(
                    <option>{e.type}</option>
                )
               })}
               
        
            </select> */}
            <input placeholder="Укажите имя товара" value={dev.name} onChange={(e)=>setDev({name:e.target.value})}></input> 
               <input placeholder="Описание товара" value={dev.desctiprion} onChange={(e)=>setDev({desctiprion:e.target.value})}></input> 
               <input placeholder="Цена" value={dev.price} onChange={(e)=>setDev({price:e.target.value})}></input> 
            
               <button onClick={clicker}>Добавить</button>

        </div>
    </div>
    )
}
