import Shop from "./pages/Shop";
import { BrowserRouter, Route,Switch  } from "react-router-dom";




function App() {

  const [menu,setMenu]=useState([{title:"О нас"},{title:"Каталог"},{title:"Войти"}])

  const authMenu=[{title:"О нас"},{title:"Каталог"},{title:"Выйти"}]

  const [theme,setTheme]=useState(false);

  return (
    <div className="App">

      <Route>
      <NavBar authMenu={authMenu} setMenu={setMenu}men={menu} theme={theme} setTheme={setTheme}/>
      <MyBurgerMenu theme={theme} setTheme={setTheme}></MyBurgerMenu>


      <Switch>
      <Shop authMenu={authMenu} setMenu={setMenu}men={menu} theme={theme} setTheme={setTheme}></Shop>
      </Switch> 


      </Route>
    </div>
  );
}

export default App;
