import NavBar from "./UI/NavBar/NavBar";
import "./styles/main.css"
import Loader from "./UI/NavBar/Loader/Loader";
import MyBurgerMenu from "./UI/BurgerMenu/MyBurgerMenu";
import React, { useContext } from "react";

import { useState,useEffect } from "react";
import Shop from "./pages/Shop";
import { BrowserRouter, Redirect, Route,Switch  } from "react-router-dom";
import { ModalReg } from "./UI/ModalReg/ModalReg";
import { MyForm } from "./UI/Form/MyForm";
import {Context,Context2} from "./index"




function App() {
  const [items,setItems]=useState([
    {id:0, name:"Apple",desctiprion:"this is a greatphone", price:"$900"},
    {id:1, name:"Samsung",desctiprion:"this is a greatphone", price:"$900"},
    {id:2,name:"Apple",desctiprion:"this is a greatphone", price:"$900"},
    {id:0, name:"Apple",desctiprion:"this is a greatphone", price:"$900"},
    {id:1, name:"Samsung",desctiprion:"this is a greatphone", price:"$900"}])

  const [menu,setMenu]=useState([{title:"О нас",href:"#"},{title:"Каталог",href:"/post"},{title:"Войти",href:"#"}])

  const authMenu=[{title:"О нас"},{title:"Каталог"},{title:"Выйти"}]

  const [theme,setTheme]=useState(false);
  const [search,setSearch]=useState("??")
  const [modal,setModal]=useState(false)
  const [modal1,setModal1]=useState(false)
  const [count,setCount]=useState(10)



  const list=items.filter((items)=>items.name.toLowerCase().includes(search.toLowerCase()))

  const{dog}=useContext(Context)

  const fun=()=>{ 
    console.log(dog)
    setCount(count=>count+1)
    dog.age=count;

  }




  return (
    <div className="App"> 




      <ModalReg modal={modal} setModal={setModal}></ModalReg>


      <NavBar modal={modal} setModal={setModal} setModal1={setModal1} list={list} search={search} setSearch={setSearch} authMenu={authMenu} setMenu={setMenu}men={menu} theme={theme} setTheme={setTheme}/>
      <MyBurgerMenu  theme={theme} setTheme={setTheme}></MyBurgerMenu>
      <Context2.Provider value={count}>
      <button style={{marginTop:"80vh"}} onClick={()=>fun()}>click</button>
      </Context2.Provider> 







    <BrowserRouter>



    <Route path="/post"><Shop items={items} setItems={setItems}  authMenu={authMenu} setMenu={setMenu}men={menu} theme={theme} setTheme={setTheme}/></Route>




    </BrowserRouter>






    </div>
  );
}

export default App;
