import React, { useContext } from "react";
import { useState } from "react";
import { Context } from "..";


export const ModalAdd=({mod,setMod})=>{ 

    const {device}=useContext(Context)
    [dev, setDev]=useState({id:i,name:'',desctiprion:"", price:""})
    
    var i=10;
    
    const clicker=async()=>{
       device.devices.push(dev)
        setName()
        setPrice()
        setDescr()
        console.log(device.devices)
        setMod(false)
    }



    return( 
        <div className={mod?'modal':'modal_none'} onClick={()=>setMod(false)}> 
        <div className="inner" onClick={(e)=>e.stopPropagation()}>
            <select>
               {device.types.map((e)=>{ 
                return(
                    <option>{e.type}</option>
                )
               })}
               
        
            </select>
            <input placeholder="Укажите имя товара" value={name} onChange={(e)=>setDev(e.target.value)}></input> 
               <input placeholder="Описание товара" value={descr} onChange={(e)=>setDescr(e.target.value)}></input> 
               <input placeholder="Цена" value={price} onChange={(e)=>setPrice(e.target.value)}></input> 
            
               <button onClick={clicker}>Добавить</button>

        </div>
    </div>
    )
}
