import React,{useState} from "react";
import cl from "./ModalReg.module.css"



export const ModalReg=({children})=>{
    
    const [modal, setModal]=useState(true)

    return( 
        <div className={modal 
            ? cl.main
            : cl.main__none}
            onClick={()=> 
            setModal(!modal)}>
            <div onClick={e=>e.stopPropagation()}className={cl.wind}>
                {children}
            </div>
        </div>
    )

}
