import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ModalAdd } from "../ModalAdd";
import cl from "./MyProduct.module.css"







const MyProduct=({device,searched,dev})=>{

    const k=Date.now();
    const [isClicked,setClicked]=useState(false);
    const dispatch=useDispatch()

    const basket=useSelector(state=>state.basket)

    const [toBasket, setBasket]=useState("")
    const localAdd=(e)=>{
        setBasket(e)
        return(console.log(toBasket))

    }

   

    

 


    console.log(dev.searched)

    return(
        <div className={cl.whole}> 



  
       {searched?
       dev.searched.map((e)=>
        <div className={cl.main} key={e.id}>
        <h2>{e.title}</h2>
        <p>{e.description}</p>
        <h3>{e.price}</h3>


       </div>

       )
       :
       device.map((e)=>
       <div className={cl.main} key={e.id} onClick={localAdd} >
        <img src={e.file}></img>
        <h2>{e.title}</h2>
        <p>{e.description}</p>
        <h3>{e.price}</h3>
        <ModalAdd mod={isClicked} setMod={setClicked}><div>{e.price}</div></ModalAdd> 


       </div>)}
       
 

        
        </div>
    )
}

const addToBasket=(e)=>{ 
    dispatch({ 
        type:"ADD_ITEM",payload:e
    })
    return(
        console.log(basket)
    )
}

export default MyProduct;
