import React from "react";
import { Link, useHistory } from "react-router-dom";
import MySearch from "../MySearch/MySearch";
import cl from "./NavBar.module.css"
import { useContext } from "react";

import { REGISTRATION_ROUTE } from "../../consts/consts";
import { Context } from "../..";



const NavBar=({list, setTheme,theme,men, setMenu,authMenu,search,setSearch,modal,setModal})=>{

   const {user}=useContext(Context)
   const history=useHistory()

   const logOut=()=>{
    user.setUser({})
    user.setIsAuth(false)
    localStorage.removeItem('token')
    history.push(REGISTRATION_ROUTE)
   }
   


    
    var i=1;

    console.log(user)
    

    return(
        <div className={cl.main}>
           
            <div className={cl.tap} onClick={()=>setTheme(!theme)}></div>
            <h2 className={cl.logo}>ВсеЗапчасти</h2>
            <ul className={cl.NavBar}>
                {men.map((p)=>
              <li key={i+90}   className={cl.li}><Link to={p.href}>{p.title}</Link></li>)}
              
            </ul>
            
            {user.isAuth?
            <button className={cl.btn} onClick={logOut}>Выйти</button>
            : 
            <button className={cl.btn}onClick={()=>history.push(REGISTRATION_ROUTE)} >Войти</button>
}
           
            <MySearch list={list} search={search} setSearch={setSearch}></MySearch>
           
        </div>
    )
}


export default NavBar;
