import React, { useContext, useEffect, useState } from "react";
import MyProduct from "../UI/MyProduct/MyProduct";
import { Context } from "../index";
import { Adder } from "../UI/Adder/Adder";
import { ModalAdd } from "../UI/ModalAdd";
import { fetchDevices } from "../http/deviceAPI";






const Shop=({theme, setTheme,searched})=>{ 

  const {device}=useContext(Context)
  const {user}=useContext(Context)

  const dev=device.devices
  const [mod,setMod]=useState(false)
  console.log(user)
  
    useEffect(()=>{ 
      
      fetchDevices().then(data=>device.setDevice(data.rows))
    },[])

    return( 
       <div  onClick={()=>setTheme(false)} className={theme?"text2":"text"}>
        
       
        
        
      <MyProduct searched={searched} device={dev} dev={device}></MyProduct>
      {user.isAuth?
      <>
      <Adder mod={mod} setMod={setMod}></Adder>
       
     <ModalAdd mod={mod} device={device} setMod={setMod}>

     <div className="inner" onClick={(e)=>e.stopPropagation()}>
            
                 <input className="input" placeholder="Укажите имя товара" value={dev.title} onChange={(e)=>setDev({...dev,title:e.target.value})}></input> 
                 <input className="input" placeholder="Описание товара" value={dev.description} onChange={(e)=>setDev({...dev,description:e.target.value})}></input> 
                 <input className="input" placeholder="Цена" value={dev.price} onChange={(e)=>setDev({...dev,price:e.target.value})}></input> 
                 <label for="id">Выберите фотографию запчасти</label>
                 <input className="input" id="file" type="file" placeholder="Выберите фотографию" value={dev.file} onChange={(e)=>setDev({...dev,file:e.target.value})}></input> 
            
               <MyButton onClick={addFunction}>Добавить</MyButton>

        </div>

     </ModalAdd>
            </>
       :
       <></>

        
    
    }
     
             
     
       </div>
    )
}

export default Shop;
