import React, {useContext, useState} from "react";
import { LOGIN_ROUTE } from "../../consts/consts";
import { login, registration } from "../../http/userAPI";
import { ModalReg } from "../ModalReg/ModalReg";
import cl from "./MyForm.module.css"

export const MyForm=({setModal,user})=> {

    const [email,setEmail]=useState("")
    const [password,setPassword]=useState("")
    
    const isLogin=location.pathname===LOGIN_ROUTE

    const click=async()=>{
        try{ 
            if(isLogin){
            let data
                data=await login(email,password)
            }
            else{ 
                data=await registration(email,password)
            }
        }catch(e){
            alert(e.message)

        }
        user.setUser(user)
        user.setIsAuth(true)
        setEmail()
        setPassword()

    } 
        
  


    
    return( 
        <div className={cl.main}> 

        <input 
           placeholder="Введите Вашем имя" 
           type="name" 
           className={cl.inputs}></input>

        <input placeholder="Введите Ваш e-mail"
           type="name" 
           className={cl.inputs}
           value={email}
           onChange={e=>setEmail(e.target.value)}></input> 

        <input placeholder="Придумайте парол" 
           type="password" 
           className={cl.inputs}
           value={password} 
           onChange={e=>setPassword(e.target.value)}></input>
        <button className={cl.BTN} 
            onClick={()=>setModal(false)}>Зарегистрироваться</button>
            
        </div>
    )
 }
