import React, { useContext } from "react";
import { useState } from "react";
import { Context } from "..";
import { createDevice } from "../http/deviceAPI";
import DeviceStore from "../store/DeviceStore";


export const ModalAdd=({mod,setMod})=>{ 

    var i=10;
    

    const {device}=useContext(Context)
    const [dev, setDev]=useState({id:i++,name:'',desctiprion:"", price:""})
    
    
    const clicker=async()=>{
       try{
       data=await createDevice(dev)
       
       device.setDevices([...device.devices,dev])
       console.log(data)
       
        setMod(false)}catch(e){ 
            alert(e.message)
        }
        setDev({id:i++,name:'',desctiprion:"", price:""})
    }



    return( 
        <div className={mod?'modal':'modal_none'} onClick={()=>setMod(false)}> 
        <div className="inner" onClick={(e)=>e.stopPropagation()}>
            {/* <select>
               {device.types.map((e)=>{ 
                return(
                    <option>{e.type}</option>
                )
               })}
               
        
            </select> */}
            <input placeholder="Укажите имя товара" value={dev.name} onChange={(e)=>setDev({...dev,name:e.target.value})}></input> 
               <input placeholder="Описание товара" value={dev.desctiprion} onChange={(e)=>setDev({...dev,desctiprion:e.target.value})}></input> 
               <input placeholder="Цена" value={dev.price} onChange={(e)=>setDev({...dev,price:e.target.value})}></input> 
            
               <button onClick={clicker}>Добавить</button>

        </div>
    </div>
    )
}
