import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ModalAdd } from "../ModalAdd";
import cl from "./MyProduct.module.css"







const MyProduct=({device,searched,dev})=>{

    const k=Date.now();
    const [isClicked,setClicked]=useState(false);
    const dispatch=useDispatch()

    const basket=useSelector(state=>state.basket)

    const addToBasket=(e)=>{ 
        dispatch({ 
            type:"ADD_ITEM",payload:e
        })
        return(
            console.log(basket)
        )
    }

    const [toBasket, setBasket]=useState("")
    const localAdd=(p)=>{
        setBasket(p)
       

    }

   

    

 


  

    return(
        <div className={cl.whole}> 



  
       {searched?
       dev.searched.map((e)=>
        <div className={cl.main} key={e.id}>
        <h2>{e.title}</h2>
        <p>{e.description}</p>
        <h3>{e.price}</h3>


       </div>

       )
       :
       device.map((p)=>
       <div className={cl.main} key={p.id} onClick={e=>localAdd(p)} >
        <img src={p.file}></img>
            <h2>{p.title}</h2>
            <p>{p.description}</p>
             <h3>{p.price}</h3>
        <ModalAdd mod={isClicked} setMod={setClicked}><div>{p.price}</div></ModalAdd> 


       </div>)}
       
 

        
        </div>
    )
}



export default MyProduct;
