import {makeAutoObservable} from "mobx";

import img from "../img/example.jpg"

export default class DeviceStore {
    constructor() {
        this._types = [{type:"Phones"},{type:"cars"},]
        this._brands = []
        this._devices = [
        {id:0, title:"Запчасть 1",description:"this is a greatphone", price:"900"},
