        {id:2,title:"Запчасть 3",description:"this is a greatphone", price:"900"},
        {id:0, title:"Запчасть 4",description:"this is a greatphone", price:"900"},
        {id:1, title:"Запчасть 5",description:"this is a greatphone", price:"900"}]
        this._selectedType = {}
        this._selectedBrand = {}
