import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ModalAdd } from "../ModalAdd";
import { MyButton } from "../UIs/MyButton/MyButton";
import cl from "./MyProduct.module.css"







const MyProduct=({device,searched,dev})=>{

    const k=Date.now();
    const [isClicked,setClicked]=useState(false);
    const dispatch=useDispatch()

    const basket=useSelector(state=>state.basket)

    const addToBasket=(p)=>{ 
        dispatch({ 
            type:"ADD_ITEM",payload:p
        })
        return(
            console.log(basket)
        )
    }

    const [toBasket, setBasket]=useState("")
    const localAdd=(p)=>{
        setBasket(p)
       

    }

    

   

    

 


  

    return(
        <div className={cl.whole}> 



  
       {searched?
       dev.searched.map((e)=>
        <div className={cl.main} key={e.id}>
        <h2>{e.title}</h2>
        <p>{e.description}</p>
        <h3>{e.price}</h3>


       </div>

       )
       :
       device.map((p)=>
       <div className={cl.main} key={p.id}  >
            <img className={cl.img} src={p.img}></img>
            <h2 className={cl.title}>{p.title}</h2>
            <p className={cl.text}>{p.description}</p>
             <h3 className={cl.price}>{p.price}</h3>
            <MyButton style={{width:"1px",
                             fontSize:"12px",
                             textAlign:"left",
                             
                             }} onClick={e=>addToBasket(p)}>В корзину</MyButton> 
        <ModalAdd  mod={isClicked} setMod={setClicked}><div>{p.price}</div></ModalAdd> 


       </div>)}
       
 

        
        </div>
    )
}



export default MyProduct;
