import React, { useContext, useState } from "react";
import MyProduct from "../UI/MyProduct/MyProduct";
import { Context } from "../index";
import { Adder } from "../UI/Adder/Adder";






const Shop=({theme, setTheme})=>{ 

  const {device}=useContext(Context)
  const {user}=useContext(Context)

  const dev=device.devices
  const [mod,setMod]=useState(false)
  console.log(user)
  
    

    return( 
       <div  onClick={()=>setTheme(false)} className={theme?"text2":"text"}>
        
       
        
        
      <MyProduct device={dev}></MyProduct>
      {user.isAuth?
      <>
      <Adder mod={mod} setMod={setMod}></Adder>
      <div className={mod?'modal':'modal_none'} onClick={()=>setMod(false)}> 
                <div className="inner" onClick={(e)=>e.stopPropagation()}>
                    <select>
                       {device.types.map((e)=>{ 
                        return(
                            <option>{e.type}</option>
                        )
                       })}
                    </select>

                </div>
            </div>
            </>
       :
       <></>

        
    
    }
     
             
     
       </div>
    )
}

export default Shop;
