import React, { useContext } from "react";
import { useState } from "react";
import { Context } from "..";


export const ModalAdd=({mod,setMod})=>{ 

    const {device}=useContext(Context)
    const [name,setName]=useState()
    const [descr,setDescr]=useState()
    const [price,setPrice]=useState()
    
    var i=0;
    
    const clicker=async()=>{
        await device.devices.push({id:i++,name:{name},desctiprion:{descr}, price:{price}})
        setName()
        setPrice()
        setDescr()
        console.log(device.devices)
        setMod(false)
    }



    return( 
        <div className={mod?'modal':'modal_none'} onClick={()=>setMod(false)}> 
        <div className="inner" onClick={(e)=>e.stopPropagation()}>
            <select>
               {device.types.map((e)=>{ 
                return(
                    <option>{e.type}</option>
                )
               })}
               
        
            </select>
            <input placeholder="Укажите имя товара" value={name} onChange={(e)=>setName(e.target.value)}></input> 
               <input placeholder="Описание товара" value={descr} onChange={(e)=>setDescr(e.target.value)}></input> 
               <input placeholder="Цена" value={price} onChange={(e)=>setPrice(e.target.value)}></input> 
            
               <button onClick={clicker}>Добавить</button>

        </div>
    </div>
    )
}
