import React from "react";
import { useState } from "react";
import cl from "./FirstTouch.module.css"

export const FirstTouch=()=> { 

    const options=[{c:"Для себя"},{c:"Для бизнеса"},{c:"Я продаю"}]
    const [isChosen,setChosen]=useState(false)

    return( 
        <div className={cl.box} onClick={(e)=>e.stopPropagation()}> 

            <h1>Для чего вам запчасти?</h1>
            <div>
            {
                options.map((p)=>
                    <h2 className={isChosen?cl.chosen
                    :cl.nonChosen} onClick={()=>setChosen(!isChosen)}>{p.c}</h2>
                )
            }
            </div>
            


        </div>
    )
}
