import React, { useContext, useEffect } from "react";
import { useState } from "react";
import { Context } from "..";
import { createDevice, fetchDevices } from "../http/deviceAPI";
import DeviceStore from "../store/DeviceStore";
import { MyButton } from "./UIs/MyButton/MyButton";


export const ModalAdd=({mod,setMod})=>{ 

    var i=10;
    

    const {device}=useContext(Context)
    const [dev, setDev]=useState({id:i++,title:'',description:"", price:""})
    
    
    const clicker=async()=>{
       try{
       let data=await createDevice(dev)
       
       device.setDevices([...device.devices,dev])
       console.log(data)
       setMod(false)
       }catch(e){ 
       alert(e.message)
       }
       setDev({id:i++,title:'',description:"", price:""})
    }

    // useEffect(()=>{ 
      
    //     fetchDevices().then(data=>device.setDevice(data.rows))
    //   },[])
    const addFunction=()=>{ 
        device.setDevices(dev)
        console.log(dev)
        setMod(false)
        return( 
            setDev({id:i++,title:'',description:"", price:""})
        )
    }
  



    return( 
        <div className={mod?'modal':'modal_none'} onClick={()=>setMod(false)}> 
        <div className="inner" onClick={(e)=>e.stopPropagation()}>
            {/* <select>
               {device.types.map((e)=>{ 
                return(
                    <option>{e.type}</option>
                )
               })}
               
        
            </select> */}
                 <input className="input" placeholder="Укажите имя товара" value={dev.title} onChange={(e)=>setDev({...dev,title:e.target.value})}></input> 
                 <input className="input" placeholder="Описание товара" value={dev.description} onChange={(e)=>setDev({...dev,description:e.target.value})}></input> 
                 <input className="input" placeholder="Цена" value={dev.price} onChange={(e)=>setDev({...dev,price:e.target.value})}></input> 
                 <input className="input" type="file" placeholder="Выберите фотографию" value={dev.price} onChange={(e)=>setDev({...dev,price:e.target.value})}></input> 
            
               <MyButton onClick={addFunction}>Добавить</MyButton>

        </div>
    </div>
    )
}
