import React from "react";
import { useState } from "react";
import cl from "./FirstTouch.module.css"

export const FirstTouch=()=> { 

    const options=[{c:"Для себя",isClicked:false},{c:"Для бизнеса",isClicked:false},{c:"Я продаю",isClicked:false}]
    const [isChosen,setChosen]=useState(false)
    const change=({p})=>{ 
        options[0].isClicked=true
        setChosen(true)
        console.log(options)

    }

    return( 
        <div className={cl.box} onClick={(e)=>e.stopPropagation()}> 

            <h1>Для чего вам запчасти?</h1>
            <div className={cl.down}>
            {
                options.map((p)=>
                    <h3 className={p.isClicked?cl.chosen
                    :cl.nonChosen} onClick={()=>change()}>{p.c}</h3>
                )
            }
            </div>
            


        </div>
    )
}
